    private void btnSubmitAdminActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubmitAdminActionPerformed
        String sql="select * from admin where username=?";
        username=txtUsername.getText();
        pass1=txtPass.getText();
        pass2=txtPass1.getText();
        role=comboRole.getSelectedItem().toString();
        pin=txtPin.getText();
        ResultSet rs=null;
        PreparedStatement ps=null;
        PreparedStatement ps2=null;
        String query=new String();
       /***************** Code to insert data into database **********/
       ////*Final Code*////
        if(pass1.compareTo(pass2)==0) {
            if(pass2.length()>=8) {
                try{
                    ps=conn.prepareStatement(sql);
                    ps.setString(1, txtUsername.getText());
                    //ps.setString(2, txtPin.getText());
                    rs=ps.executeQuery();
                    if(rs.next()) {
                        String user=rs.getString("username");
                        String pn=rs.getString("pin");
                        if(username.compareTo(user)==0) {
                            JOptionPane.showMessageDialog(null, "Username not Available!! Try another.");
                        }
                    }
                    else{
                        query="Insert into admin values('"+username+"','"+pass1+"','"+role+"','"+pin+"')";  
                        ps=conn.prepareStatement(query);
                        ps.execute();
                        JOptionPane.showMessageDialog(null,"Data Inserted Successfully");
                    }
                }
                catch(Exception e) {
                    JOptionPane.showMessageDialog(null, e);
                } finally {
                    try {
                        rs.close();
                        ps.close();
                    }catch(Exception e) {
                
                    }
                }
            }
            else{
                JOptionPane.showMessageDialog(null, "Password must be minimum 8 characters long!!");
            }
        }
        else{
            JOptionPane.showMessageDialog(null, "Password doesn't match!!");
        }
    }//GEN-LAST:event_btnSubmitAdminActionPerformed

