    private void btnSubmitStActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubmitStActionPerformed
        String sql="select * from student where id=?";
        name=txtName.getText();
        id=txtID.getText();
        dept=txtDept.getText();
        age=txtDay.getText()+"-"+comboMonth.getSelectedItem().toString()+"-"+txtYear.getText();
        address=txtAdd.getText();
        gender=comboGen.getSelectedItem().toString();
        contact=txtCon.getText();
        blood=txtBlood.getText();
        pin=txtPin.getText();
        ResultSet rs=null;
        PreparedStatement ps=null;
        String query=new String();
        /////////////***Test Code**/////////////
        try{
            ps=conn.prepareStatement(sql);
            ps.setString(1, id);
            rs=ps.executeQuery();
            if(rs.next()) {
                String iid=rs.getString("id");
                if(id.compareTo(iid)==0) {
                    JOptionPane.showMessageDialog(null, "This ID has been registered!!");
                }
            }
            else{
                query="Insert into student values('"+name+"','"+id+"','"+dept+"','"+age+"','"+address+"','"+gender+"','"+contact+"','"+blood+"','"+pin+"')";
                ps=conn.prepareStatement(query);
                ps.execute();
                JOptionPane.showMessageDialog(null,"Data Inserted Successfully");
            }
        }
        catch(Exception e) {
            JOptionPane.showMessageDialog(null, e);
        } finally {
            try {
                rs.close();
                ps.close();
            }catch(Exception e) {
                
            }
        }
        
    }//GEN-LAST:event_btnSubmitStActionPerformed

