    private void txtIDKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtIDKeyReleased
        try{
            String sql="select * from student where id=?";
            pst=conn.prepareStatement(sql);
            pst.setString(1, txtID.getText());
            rs=pst.executeQuery();
            if(rs.next()) {
                String name=rs.getString("name");
                txtSName.setText(name);
                String id=rs.getString("id");
                txtID2.setText(id);
            }
        }catch(Exception e) {
            
        } finally {
            try {
                rs.close();
                pst.close();
            }catch(Exception e) {
                
            }
        }
    }//GEN-LAST:event_txtIDKeyReleased

    private void txtAcc1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtAcc1KeyReleased
        try{
            String sql="select * from book where accession=?";
            pst=conn.prepareStatement(sql);
            pst.setString(1, txtAcc1.getText());
            rs=pst.executeQuery();
            if(rs.next()) {
                String acc=rs.getString("accession");
                txtAcc.setText(acc);
                String bname=rs.getString("title");
                txtBName.setText(bname);
                String isbn=rs.getString("isbn");
                txtIsbn.setText(isbn);
            }
        }catch(Exception e) {
            
        } finally {
            try {
                rs.close();
                pst.close();
            }catch(Exception e) {
                
            }
        }
    }//GEN-LAST:event_txtAcc1KeyReleased

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String sql="select * from issue where id=?";
        acc=txtAcc.getText();
        id=txtID2.getText();
        sname=txtSName.getText();
        isbn=txtIsbn.getText();
        //acc=txtAcc.getText();
        bname=txtBName.getText();
        date=comboDate.getSelectedItem().toString()+"-"+comboMonth.getSelectedItem().toString()+"-"+comboYear.getSelectedItem().toString();
        ResultSet rs=null;
        PreparedStatement ps=null;
        String query=new String();
        if(acc.isEmpty()&&id.isEmpty()&&sname.isEmpty()&&isbn.isEmpty()&&bname.isEmpty()&&date.isEmpty()) {
            JOptionPane.showMessageDialog(null, "All Fields should be filled up!!");
        }
        else{
            try{
                ps=conn.prepareStatement(sql);
                ps.setString(1, acc);
                rs=ps.executeQuery();
                query="Insert into issue values('"+id+"','"+sname+"','"+acc+"','"+bname+"','"+isbn+"','"+date+"')";
                ps=conn.prepareStatement(query);
                ps.execute();
                JOptionPane.showMessageDialog(null,"Book has been successfully Issued!!");
            }catch(Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }finally {
                try {
                    rs.close();
                    ps.close();
                }catch(Exception e) {

                }
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

